mapper.select:
SELECT * FROM chat_messages
WHERE (
    (sender_id = #{user1Id} AND receiver_id = #{user2Id})
    OR (sender_id = #{user2Id} AND receiver_id = #{user1Id})
)
AND create_time <= #{beforeTime}
ORDER BY create_time DESC
LIMIT #{limit}

SELECT * FROM chat_messages
WHERE (
    (sender_id = #{user1Id} AND receiver_id = #{user2Id})
    OR (sender_id = #{user2Id} AND receiver_id = #{user1Id})
)
AND create_time > #{afterTime}
ORDER BY create_time DESC
LIMIT #{limit}

SELECT id, sender_id, receiver_id, content, message_type, is_read, client_msg_id, create_time, update_time
FROM chat_messages 
WHERE receiver_id = #{userId}
  AND create_time > #{lastLogoutTime}
  AND is_read = false
ORDER BY create_time ASC
LIMIT #{limit}

mapper.update:
UPDATE chat_messages 
SET is_read = TRUE, update_time = NOW()
WHERE receiver_id = #{userId} 
  AND id IN

    #{messageId}

  AND is_read = FALSE

mapper.select:
SELECT id, sender_id, receiver_id, content, message_type, is_read, client_msg_id, create_time, update_time FROM chat_messages WHERE id IN
#{messageId}
ORDER BY create_time ASC

SELECT COUNT(*) 
FROM chat_messages 
WHERE receiver_id = #{userId}
  AND create_time BETWEEN #{startTime} AND #{endTime}
